<script>
	$(window).on("load resize scroll", function(){
		var from_top = $(window).scrollTop();
		var section_height1 = $("section.mercury").offset().top;
		var section_height2 = $("section.team").offset().top;
		if(from_top > section_height1){
			$("section.mercury, section.saving_bob").addClass("colored");
		}else{
			$("section.mercury, section.saving_bob").removeClass("colored");
		}
		if(from_top > section_height2){
			$("section.team").addClass("colored");
		}else{
			$("section.team").removeClass("colored");
		}
	});
</script>

